 and had_h = #{had_h}


 and had_d = #{had_d}


 and had_a = #{had_a}


 and status=#{status}


 and leaguename = #{leaguename}


 and (had_bet=#{had_bet})


order by starttime

        limit #{row_start},#{page_size}

		SELECT count(*) from 

        	t_loto_fn_eth  
