	        t_loto_sis_e_eth  
	        t_loto_sis_e_gto
	        t_loto_sis_e_uz   

    set issue=#{issue},electronic_code=#{electronic_code},one=#{one},two=#{two},three=#{three},one_d=#{one_d},two_d=#{two_d},three_d=#{three_d},one_p=#{one_p},two_p=#{two_p},three_p=#{three_p}
		WHERE  issue=#{issue}

