mapper.select:
		SELECT id,channelnum,channelname,channelstate,createtime from t_market_channel


 and id=#{id}


 and channelnum=#{channelnum}


 and channelname = #{channelname}


 and channelstate = #{channelstate}


 and date(createtime)=date(#{createtime})


 and createtime between #{start_createtime} and #{end_createtime}


order by id


        limit ${row_start},${page_size}

		SELECT count(*) from t_market_channel


 and id=#{id}


 and channelnum=#{channelnum}


 and channelname = #{channelname}


 and channelstate = #{channelstate}


 and date(createtime)=date(#{createtime})


 and createtime between #{start_createtime} and #{end_createtime}


order by id
